function safePop(stack) {
  if (stack.length === 0) return 'UNDERFLOW';
  return stack.pop();
}
console.log(safePop([])); // UNDERFLOW

function isValidParentheses(s) {
  const st = [];
  for (const ch of s) {
    if (ch === '(') st.push(ch);
    else if (ch === ')') {
      if (!st.length) return false;
      st.pop();
    }
  }
  return st.length === 0;
}
