class IntStack(private val capacity: Int) {
    private val arr = IntArray(capacity)
    private var top = -1

    fun isEmpty() = top == -1

    fun push(x: Int) {
        arr[++top] = x
    }

    fun pop(): Int {
        if (isEmpty()) throw NoSuchElementException("underflow")
        return arr[top--]
    }
}
