public class Main {
    public static void main(String[] args) {
        int[] nums = {1, 3, 3, 2, 5, 3};
        int maxVal = 5;

        int[] freq = new int[maxVal + 1];

        for (int i = 0; i < nums.length; i++) {
            int value = nums[i];
            if (value >= 0 && value <= maxVal) {
                freq[value]++;
            }
        }

        for (int i = 0; i <= maxVal; i++) {
            System.out.println(i + " : " + freq[i]);
        }
    }
}
