fun twoSum(nums: IntArray, target: Int): IntArray {
    val map = HashMap<Int, Int>()

    for (i in nums.indices) {
        val need = target - nums[i]
        val j = map[need]
        if (j != null) return intArrayOf(j, i)
        map[nums[i]] = i
    }

    return intArrayOf(-1, -1)
}
